package org.mo.eng.cache;

//============================================================
// <T>缓冲控制台接口。</T>
//============================================================
public interface ICacheConsole
{
   //============================================================
   // <T>测试是否允许。</T>
   //
   // @return 是否允许
   //============================================================
   boolean isEnable();

   //============================================================
   // <T>获得代码。</T>
   //
   // @return 代码
   //============================================================
   String code();

   //============================================================
   // <T>收集一个内存频道。</T>
   //
   // @return 内存频道
   //============================================================
   ICacheChannel alloc();

   //============================================================
   // <T>释放一个内存频道。</T>
   //
   // @param channel 内存频道
   //============================================================
   void free(ICacheChannel channel);
}
