package org.mo.eng.cache.memory;

import org.mo.eng.cache.ICacheChannel;

//============================================================
// <T>缓冲接口。</T>
//============================================================
public interface ICacheConsole
{
   //============================================================
   // <T>测试是否允许。</T>
   //
   // @return 是否允许
   //============================================================
   boolean isEnable();

   //============================================================
   // <T>获得代码。</T>
   //
   // @return 代码
   //============================================================
   String code();

   //============================================================
   // <T>获得主键限制。</T>
   //
   // @return 主键限制
   //============================================================
   int keyLimit();

   //============================================================
   // <T>获得内容限制。</T>
   //
   // @return 内容限制
   //============================================================
   int valueLimit();

   //============================================================
   // <T>收集一个内存频道。</T>
   //
   // @return 内存频道
   //============================================================
   ICacheChannel alloc();

   //============================================================
   // <T>释放一个内存频道。</T>
   //
   // @param channel 内存频道
   //============================================================
   void free(ICacheChannel channel);

   void register(Class<?> clazz,
                 String name,
                 ICache cache);

   ICache find(Class<?> clazz,
               String name);
}
